way.'),(12300,338,'_blocks_1_masked_content','field_5967cdf3c143f'),(12301,338,'blocks_2_content','<div class=\"medium-6 medium-centered text-center\">\r\n<h1>What Makes\r\nUs Genius?</h1>\r\n<p class=\"headline--description text--primary-light\">Genius isn\'t just gluten free. It\'s bread that tastes like bread, made with hearty ingredients for rich flavor and texture that\'s just right. The result is a golden-crusted, fresh-smelling, mind-bogglingly delicious loaf of gluten free goodness.</p>\r\n\r\n</div>'),(12302,338,'_blocks_2_content','field_57b66234b32d6'),(12303,338,'blocks_2_cta_arrow','0'),(12304,338,'_blocks_2_cta_arrow','field_59245a3bddfed'),(12305,338,'blocks_2_width','container'),(12306,338,'_blocks_2_width','field_58b9a2429eb95_field_58b9a114a1e6b'),(12307,338,'blocks_2_height','auto'),(12308,338,'_blocks_2_height','field_58b9a2429eb95_field_58b9a13ca1e6c'),(12309,338,'blocks_2_enable_background_overlay','0'),(12310,338,'_blocks_2_enable_background_overlay','field_58b9a2429eb95_field_58b9a084a1e67'),(12311,338,'blocks_2_background_image',''),(12312,338,'_blocks_2_background_image','field_58b9a2429eb95_field_58b9a0a5a1e68'),(12313,338,'blocks_2_select_brand_color','accent-light'),(12314,338,'_blocks_2_select_brand_color','field_58b9a2429eb95_field_5967cba998df0'),(12315,338,'blocks_2_background_style','cover'),(12316,338,'_blocks_2_background_style','field_58b9a2429eb95_field_58e28ee4f5088'),(12317,338,'blocks_2_repeat_background','0'),(12318,338,'_blocks_2_repeat_background','field_58b9a2429eb95_field_58e28f12f508a'),(12319,338,'blocks_2_padding','1'),(12320,338,'_blocks_2_padding','field_58b9a2429eb95_field_58b9a0f9a1e6a'),(12321,338,'blocks_2_additional_classes',''),(12322,338,'_blocks_2_additional_classes','field_58b9a2429eb95_field_58b9a15aa1e6d'),(12323,338,'blocks_2_custom_id','why-genius'),(12324,338,'_blocks_2_custom_id','field_58b9a2429eb95_field_58b9a16fa1e6e'),(12325,338,'blocks_2_disable_block','0'),(12326,338,'_blocks_2_disable_block','f