INSERT INTO `wp_postmeta` VALUES (6991,264,'short_description','This Italian peasant-style salad is all about having the right bread, the juiciest tomatoes, the ripest peppers and the freshest basil. Add in a bit of good olive oil and vinegar to these summer flavors for a simple, rustic dish that will impress any dinner guest.');
INSERT INTO `wp_postmeta` VALUES (6992,264,'_short_description','field_59678c333d345');
INSERT INTO `wp_postmeta` VALUES (6993,264,'directions','<ol>\r\n 	<li>With a fork, mash the garlic, anchovies and capers to a puree on a chopping board. Place in the bottom of a salad bowl.</li>\r\n 	<li>Add the diced pepper and red onion to the anchovy puree. Stir in the olive oil and vinegar. Cover and chill for 15 minutes.</li>\r\n 	<li>Place the toasted bread squares in a separate bowl. Mash half of the diced tomatoes and mix through the bread. Let sit for 15 minutes to allow the bread to soak up the juices.</li>\r\n 	<li>Add the remaining diced tomatoes and cucumber to the peppers and anchovy mixture. Stir in the soaked bread and shredded basil. Taste and season with salt and pepper.</li>\r\n 	<li>Serve with green salad and grilled or grilled meat.</li>\r\n</ol>');
